import React, {useState, useCallback, useEffect} from 'react';
import axios from "axios";
import "./styles/Modal.scss";

const PwdCheckModal = ( props: any ) => {
	const { open, close, chatRoomID} = props;

	const [MyID, setMyID] = useState('')
	const [PWD, setPWD] = useState("");
	const [WorngPWD, setWrongPWD] = useState(false)

	useEffect(() => {
		const id = sessionStorage.getItem('nickname')
		if (id) setMyID(id)
	})

	const handelPWD = useCallback((e:any) => {
		setPWD(e.target.value)
	}, []);

	function confirmPWD(){
		axios.put('/Lobby/enter/' + chatRoomID, {id:MyID, password:PWD})
		.then((res) => {
			document.location.href = '/Chat';
			sessionStorage.setItem('roomName', chatRoomID)
		})
		.catch((err) => {
			setWrongPWD(true)
		})
	}

	return (
		<div className={ open ? 'openModal Modal' : 'Modal' }>
			{ open ? (
				<section>
					<header>
						Enter Password
						<button className="close" onClick={close}> &times; </button>
					</header>
					<main>
						<input onChange={handelPWD}></input>
						{WorngPWD ? <label className="error">틀린 비밀번호입니다.</label> : null}
					</main>
					<footer>
						<a className="btn btn-primary" onClick={confirmPWD}><b>Enter</b></a>
					</footer>
				</section>
			) : null }
		</div>
	)
}

export default PwdCheckModal
