//import logo from './logo.svg';
import React, {useState} from "react";
import './App.scss';
import Chat from "./Chat/Chat";
import Admin from "./Admin/Admin";
import Lobby from "./Lobby/Lobby"
import Login from './Login/Login'
import "./Chat/styles/global.css";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
const redirect_url = `https://api.intra.42.fr/oauth/authorize?client_id=bdfe71f0d292f9a780b44094736aaf3f844a65813080ff82b60e00bb29143d01&redirect_uri=http%3A%2F%2Flocalhost%3A8080%2Flog%2Fin&response_type=code`;

function App() {
	return (
	<div className="App">
		<Router>
		  <Switch>
			<Route exact path="/" render={() => 
				//document.location.href='/log/auth'
				window.location.href = redirect_url
				//<Redirect to='log/auth' />
			}/>
			<Route path="/Admin" exact render={() =>
				sessionStorage.getItem('intraID') ? 
					<Admin/> : 
					<Redirect to={{
						pathname: '/log/auth'
					}} />
			}/>
			<Route path="/Lobby" exact render={() =>
				<Lobby/>
			}/>
			<Route path="/Chat" exact render={() =>
				<Chat/>
			}/>
			<Route
				render={({location}) => (
					  <div>
						Not Found {location.pathname}
					</div>
				)}
			/>
		  </Switch>
		</Router>
	</div>
	);
  }


export default App;
