import React, {useEffect, useState}from "react";
import "./styles/Admin.scss";
import 'bootstrap/dist/css/bootstrap.min.css';
import {allImages} from '../Images/Images';
import axios from "axios";

const Admin = () => {
	const [IntraID, setIntraID] = useState('')
	const [Nickname, setNickname] = useState('')
	const [Icon, setIcon] = useState('')
	const [SignUp, setSignUp] = useState(true)

	useEffect(() => {
		const intra_id = sessionStorage.getItem("IntraID")
		if (intra_id) setIntraID(intra_id);

		const id = sessionStorage.getItem("Nickname");
		if (id) {
			setNickname(id);
			setSignUp(false);
		}
	})

	const handleNicknameChange = (e:any) => {
		console.log(e.target.value)
		setNickname(e.target.value);
	};
	const handleIconChange = (e:any) => {
		console.log(e.target.value)
		setIcon(e.target.value);
	};

	sessionStorage.setItem("Nickname", Nickname);

	async function saveAdmin(isSignUp: boolean){
		const body = {
			nickname: Nickname,
			state: 'on',
			icon: Icon,
			//...
		}
		if (isSignUp)
			await axios.put('/Admin/' + IntraID, body)
		else
			await axios.post('/Admin/' + IntraID, body)
	}

	return (
	<div className="Admin-container">
		<label><b>ID</b></label>
		<input onChange={handleNicknameChange}></input>

		<div className="icon-box">
		{allImages.map(({value, src}) =>
			<span className="icon">
				<input type="radio" name="icon" value={value} onChange={handleIconChange} className="icon-input"/>
				<img src={src} width="50" height="50" className="icon-img"></img>
			</span>
		)}
		</div>

		<div className="Join-button">
			<a className="btn btn-dark" href="/Lobby" onClick={() => saveAdmin(SignUp)}><b>OK</b></a>
		</div>
	</div>
	);
};

export default Admin;
