import {useEffect, useState, useCallback} from "react";
import axios from "axios";
import useSwr from 'swr';
import lock from '../Images/lock.png'
import unlock from '../Images/unlock.png'
import "./styles/Lobby.scss"

const ChatList = () => {
	const fetcher = async (url:string) => {
		const res = await axios.get(url)
		return res.data;
	}

	const {data, error} = useSwr<{title:string, num:number, security:string}[]>('Lobby/chatList', fetcher)

	return (
	<>
		{data?.map(chatRoom=>
			<button type="button" className="roomList" onClick={() => {
				document.location.href = '/Chat';
				sessionStorage.setItem('roomName', chatRoom.title)
			}}>
			<span className="roomList-left">
				<div className="roomList-num">{chatRoom.num}</div>
			</span>
			<span className="roomList-right">
				<div>
					{chatRoom.security != "protected" ? <img src={lock} width="20" height="20"/> : <img src={unlock} width="20" height="20"/>}
				</div>
				<div className="player">
					{chatRoom.title}
				</div>
			</span>
			</button>
		)}
	</>
	)
}

export default ChatList
