import axios from "axios";
import React, { useEffect, useState } from "react";
import './styles/Auth.scss'

const Auth = () => {

	const [IntraID, setIntraID] = useState('')
	const [AuthCode, setAuthCode] = useState('')

	useEffect(() => {
		const url = window.location.href.split('?')
		const intra_str = url[1]
		const intra_id = intra_str.split('=')
		//setIntraID(intra_id[1])
		//sessionStorage.setItem('intraID', intra_id[1])
	})

	const handleAuthCodeChange = (e:any) => {
		setAuthCode(e.target.value);
		console.log(e.target.value)
	};

	const authCheck = async() => {
		await axios.post('/log/2-factor-auth', {
			intra_id: IntraID,
			auth_value: AuthCode
		})
	}


	return (
	<>
		intra_id@student.42seoul.kr로 메일이 발송되었습니다.
		<input placeholder="인증 코드를 입력해주세요" onChange={handleAuthCodeChange}/>
		<button onClick={authCheck}>확인</button>
	</>
	)

}

export default Auth
