//import logo from './logo.svg';
import React, {useState} from "react";
import './App.scss';
import Chat from "./Chat/Chat";
import Admin from "./Admin/Admin";
import Lobby from "./Lobby/Lobby"
import "./Chat/styles/global.css";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Auth from './Auth/Auth'
import {Beforeunload} from 'react-beforeunload';
import axios from "axios";

function App() {

	const redirect_auth = () => {
		window.location.href = '/log/auth'
		return null
	}

	async function Logout(event:any){
		event.preventDefault()
		const intra_id = sessionStorage.getItem('intraID')
		if (intra_id)
			await axios.patch('log/out' , {intra_id : intra_id})
	}

	return (
	<div className="App">
		<Router>
		  <Switch>
			<Route exact path="/" render={() =>
				sessionStorage.getItem('intraID') ? 
				<Beforeunload onBeforeunload={(event) => Logout(event)}>
					<Lobby/>
				</Beforeunload> 
				: redirect_auth()
			}/>
			<Route exact path="/2-factor-auth" render={() =>
				<Auth/>
			}/>
			<Route path="/Admin" exact render={() =>
				sessionStorage.getItem('intraID') ? 
				<Beforeunload onBeforeunload={Logout}>
					<Admin/> 
				</Beforeunload>
				: redirect_auth()
			}/>
			<Route path="/Lobby" exact render={() =>
				sessionStorage.getItem('intraID') ? 
				<Beforeunload onBeforeunload={Logout}>
					<Lobby/> 
				</Beforeunload>
				: redirect_auth()
			}/>
			<Route path="/Chat" exact render={() =>
				sessionStorage.getItem('intraID') ? 
				<Beforeunload onBeforeunload={Logout}>
					<Chat/> 
				</Beforeunload>
				: redirect_auth()
			}/>
			<Route
				render={({location}) => (
					  <div>
						Not Found {location.pathname}
					</div>
				)}
			/>
		  </Switch>
		</Router>
	</div>
	);
  }


export default App;
