import axios from 'axios';
import React, {useState, useCallback} from 'react';
import "./styles/ChatOwnerModal.scss";

const ChatOwnerModal = ( props: any ) => {
	const { open, close, chatRoomID} = props;

	const [BannedUsers, setBannedUsers] = useState<string[]>(['jinkim', 'taekkim'])

	async function changePWD(){
		//await axios.post('chat/' + chatRoomID + '/admin/')
		close();
	}

	const [RoomPWD, setRoomPWD] = useState('');
	const handelRoomPWD = useCallback((e:any) => {
		setRoomPWD(e.target.value)
	}, []);

	return (
		<div className={ open ? 'openModal Modal' : 'Modal' }>
			{ open ? (
				<section>
					<header>
						Setting
						<button className="close" onClick={close}> &times; </button>
					</header>
					<main>
						Banned users
						<div className="ban-box">
							{BannedUsers?.map(user => {
								{user}
							})}
						</div>
						<hr />
						new password:&nbsp;
						<input onChange={handelRoomPWD} value={RoomPWD}></input>
						&nbsp;
						<a className="btn btn-primary btn-sm" onClick={changePWD}><b>Change</b></a>
					</main>
					<footer>
					</footer>
				</section>
			) : null }
		</div>
	)
}

export default ChatOwnerModal
